mapper.select:
SELECT t1.*, t2.learn_date learnDate, t2.learn_length learnLength, t2.teachplan_id teachplanId, t2.teachplan_name teachplanName, IFNULL(t2.learn_date,t1.create_date) orderdate
FROM xc_course_tables t1 LEFT JOIN xc_learn_record t2 ON t1.course_id = t2.course_id AND t2.id =( SELECT id FROM xc_learn_record a WHERE a.course_id = t1.course_id ORDER BY a.learn_date DESC LIMIT 1 ) WHERE
t1.user_id = #{userId}
and t1.course_type=#{courseType}
and DATEDIFF(t1.validtime_end,now())<30
and DATEDIFF(t1.validtime_end,now())<1
order by orderdate desc
ORDER BY t1.create_date desc
limit #{startIndex},#{size}

SELECT count(1) FROM xc_course_tables t1 LEFT JOIN xc_learn_record t2 ON t1.course_id = t2.course_id AND t2.id =( SELECT id FROM xc_learn_record a WHERE a.course_id = t1.course_id ORDER BY a.learn_date DESC LIMIT 1 ) WHERE
t1.user_id = #{userId}
and t1.course_type=#{courseType}
and DATEDIFF(t1.validtime_end,now())<30
and DATEDIFF(t1.validtime_end,now())<1
